css:
@-moz-document domain("wartanews.com") {
#scroller, div[style='width:728px;margin:20px auto 0 auto;'], div [style='width:300px;height:250px;']{display:none!important}

#ticker {display:none;height:1px; font-size:0px; overflow:hidden}
body { font-family: 'HelveticaNeue-Light', 'Helvetica Neue Light', 'Helvetica Neue', Helvetica, Arial, 'Lucida Grande', sans-serif; font-weight: 300;}

a {
font-weight: 300;
}
a, :link, :visited {
text-decoration: none;
color: #18A1C4;
}

a:hover {
color: #042841;
outline: 0;
}

#top-navigation #nav li a { font-family: 'HelveticaNeue-Light', 'Helvetica Neue Light', 'Helvetica Neue', Helvetica, Arial, 'Lucida Grande', sans-serif; font-weight: 300;
font-size: 13px;
color: #222;
height: 30px;
line-height: 30px;
float: left;
width: auto;
padding: 0 13px;
display: block;
text-align: center;
}
}
