css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://slinging.org/forum/") {

body {
    width: 100% !important;
}

#container {
    width: 100% !important;
}

div.headerwelc {
    display: none !important;
}

div.displaycontainer {
    padding: 0 !important;
}

div.displaycontainer table tr td:first-child {
    width: 6em !important;
}

div.displaycontainer table tr td:first-child img {
    max-width: 6em !important;
}

div.scroll img {
    width: 100%;
}

.message {
    font-size: 10pt !important;
}

/* Hiding some columns from the board. */
div.seperator table.bordercolor tr td:nth-child(1) {
    display: none;
}
div.seperator table.bordercolor tr td:nth-child(2) {
    display: none;
}
div.seperator table.bordercolor tr td:nth-child(4) {
    display: none;
}
div.seperator table.bordercolor tr td:nth-child(5) {
    display: none;
}
div.seperator table.bordercolor tr td:nth-child(6) {
    display: none;
}
}
