@-moz-document domain("enjoy.your-clock.com") {
@-webkit-keyframes breath{
0% {text-shadow: 0 0 20px #00FF00;}
50% {text-shadow: 0 0 50px #00FF00;}
100% {text-shadow: 0 0 20px #00FF00;}
}
@-webkit-keyframes blinkComma{
0% {visibility: visible;}
49% {visibility: visible;}
50% {visibility: hidden;}
100% {visibility: hidden;}
}

html, body{
-webkit-animation:  breath 5s ease-in-out infinite;
}
.E{
-webkit-animation: blinkComma 1s infinite;
}
}
