@namespace url(http://www.w3.org/1999/xhtml);
/*Removes the Premium and NC Mall tabs on Neopets.*/
@-moz-document domain("neopets.com") {
  .nav_image a[href^="http://ncmall.neopets.com/mall/"] img{display:none;}
  .nav_image a[href="http://nc.neopets.com/membership/"] img{display:none;}
  .nav_image a[href="/mall/shop.phtml?page=&cat="] img{display:none;}
  #template_nav {padding-left:45px; width:unset;}
  li.nav_image {margin:0 5px;}
}
@-moz-document url-prefix("http://ncmall.neopets.com") {
  #header .user.medText {width: 500px;}
}
