css:
@-moz-document domain("youtube.com") {
body {background:#FFF!important;}
* {border-radius:0!important; box-shadow:none!important;}

.social-service, .yt-nav-dark, .yt-nav-dark .yt-nav-item:hover {background-image:none!important;}

.branded-page-v2-container {margin-left: -1px!important;}
.branded-page-v2-header {margin: -1px!important;}
.branded-page-v2-top-row, .branded-page-v2-primary-col-header-container { margin-bottom:0!important;}
.branded-page-v2-body {padding-top: 30px!important;}

#guide-main .guide-module-content {padding:0!important;}
#guide-container h3 {color:#333!important;}

.video-list-item { margin-bottom: 4px!important; background: whiteSmoke!important; border: 1px solid #E8E8E8!important;}
.video-list-item a { padding: 5px!important;}

#watch-description-toggle .yt-uix-button-text, #watch-like, #watch-dislike, #footer-main .pickers .yt-uix-button {border: 1px solid lightGrey!important; background: #F8F8F8!important;}
#footer-main .pickers .yt-uix-button {height: 26px!important;}

#yt-hitchhiker-feedback a {background-image:none!important; background:#333!important; border:none!important;}

.guide-enabled #footer { margin: 40px 0 0 180px!important; padding: 15px 0 30px!important; border-top: 1px solid #DADADA!important;}
#footer { margin-top: 40px!important; padding: 15px 0 30px!important; border-top: 1px solid #DADADA!important;}

.account-sidebar-section li {margin-bottom:1px!important; height: 30px;}
.account-sidebar-section li .selected, .account-sidebar-section li a:hover { border: 1px solid #E8E8E8!important;
background: #F1F2F1!important; border-width: 1px 0!important;}

.yt-nav-dark {background: #555!important;}
.yt-nav-dark .yt-nav-item:hover {background: #444!important;}
}
