css:
@-moz-document domain("veetle.com") {
html {background-color:#000!important;}

#header {background:-moz-linear-gradient(center top , #595959, #222) repeat scroll 0 0 transparent!important; border-bottom-color:#333!important; width:100%!important;}
.spriteCommon {background:url('http://www.userlogos.org/files/logos/DaveyJake/Veetle_Red.png') no-repeat 0 -31px!important; background-size:100% 334%!important;}
#navigation li a, #account li > a span, #username *, #channelHeader *, #channelDescription {color:#999!important; text-shadow:none!important;}
ul#account {position:absolute; right:0px!important;}

#container.container {width:100em!important;}
#listingContent {margin:0!important;}
ol#streams li.stream {margin-left:5px!important; margin-right:5px!important;}

.button, .tabs li:not(.selected) {background: -moz-linear-gradient(center top , #222, #595959) repeat scroll 0 0 transparent!important; border-color:#999!important; text-shadow:none!important; box-shadow:none!important;}
.commentHead, #comments, #schedule {background:#222!important;}
#commentContainer {border:1px solid #fff!important;} #commentWrite {border-color:#fff!important; border-bottom-width:2px!important;}
.tabs li.selected {background:-moz-linear-gradient(center top , #595959, #222) repeat scroll 0 0 transparent!important; border-bottom-color:#333!important; text-shadow:none!important; box-shadow:none!important;}
.tabs li.selected a, #comments div {color:#ccc!important;}
.tabs li:not(.selected) a {color:#959595!important;}

.underColumn h2, .underColumn h2 div {background:#222!important; border-color:#222!important; color:#999!important;}
#broadcasterInfo table tr th, #schedule li:not(.selected) em {color:#888!important;}
#broadcasterInfo table tr td, #schedule li:not(.selected) dt, #comments li {color:#bbb!important;}

#playerChrome, #playerChrome a:not(#resolutionCurr), #playerChrome div:not(#resolutions), #playerChrome span:not(#fileTime) {display:block!important;}
#fileTime, #player > #plugin > #flashPlayer {display:none!important;}
}
