css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("businessweek.com") {


body,select,input,textarea,#article_body_container,#story_body,p, a, a:active, a:visited,#dsq-content,.dsq-comment-footer-right,
#dsq-content ul, #dsq-content li, #dsq-content ol, #dsq-content cite, #dsq-content div, #dsq-content table, #dsq-content td, #dsq-content th, #dsq-content p, #dsq-content a, #dsq-content img, #dsq-content caption  {
color:#c2f8fc !important;background-color:black !important;
}

#dsq-content, .dsq-comment-footer-left { visibility:visible !important;}
}
