css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.neopets.com/neoboards/") {

/*
Changes the font and size of text, including the:
Board topic, board author, reply count, last post time
Removes unnecessary bold style and colours.
Posts take up full width of page.
*/

.blistSmall, .blistTopic {
font: 9pt Verdana, Geneva, sans-serif !important;
text-transform: lowercase !important;}
#boards_table {width:100% !important;}
.topicSmall + .topicSmall {display:none !important;}
.topicPosted table {height:14px !important;}
.topicAuthor table tbody tr:nth-child(1) b {color:black !important; font-weight:normal;}
.topicAuthor table tbody tr:nth-child(2) {display:none;}
.topicAuthor table tbody tr:nth-child(3) {color:transparent;}
.sf a b {position:relative; top:5px;}
.topicPosted {height:0px !important;}
.topicPosted strong {font-weight:normal;}
}
