css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fearless-assassins.com") {

#user_bar 
{
    background: #202020 !important;
    text-shadow: none !important;
    border-bottom: none !important;
}

#branding
{
    background: #D9028E !important;
    height: 150px !important;
}

.wrapper
{
    width: 100% !important;
    max-width: 100% !important;
}


#primary_nav, #content, #secondary_navigation
{
    margin-left: 5% !important;
    margin-right: 5% !important;
    padding-left: 0px !important;
}

#logo
{
    margin-left:  6% !important;
    margin-top: 10px !important;   
    height: 90px !important;
}

.content
{   
    background: #333 !important; 
}

#links a:link , #links a:visited, .notnew a:link, .notnew a:visited, .unread a:link, .unread a:visited, .list_content a:link, .list_content a:visited, h4 a:link, h4 a:visited, .skin_link a:link, .skin_link a:visited, .skin_link, .ipsSideMenu a:link, .ipsSideMenu a:visited, .name a:link, .name a:visited,  #shoutbox-shouts-table a:link, #shoutbox-shouts-table a:visited
{
    color: #FFA2d3 !important;
}

#dname_history a:link, #dname_history a:visited, .mod_links a:link, .mod_links a:visited,  .__showform , .__showAll, .ipsType_small a:link, .ipsType_small a:visited
{
    color: #FFA2d3 !important;
}

#links a:hover, .notnew a:hover, .unread a:hover,  h4 a:hover, .skin_link a:hover, #dname_history a:hover, .mod_links a:hover, .__showform:hover, .__showAll:hover
{
    color: #FF6EC7 !important;
}

.post a:link, .post a:visited 
{
    color: #c189ff !important;
}

.post a:hover, .ipsSideMenu a:hover
{
    color: #FF6EC7 !important;
}

.last_post span
{
    color: #FFA2d3;
}

span.title, span.number, span.row_data
{
    color: rgb(188,188,188) !important;
}

.custom_fields a:link, .custom_fields a:visited,.statistics_brief a
{
    color: #993F75 !important;
}

.custom_fields a:hover
{
    color: #D9028E !important;
}

.maintitle
{ 
    background: #D9028E !important;
}

.maintitle a
{
    color: #FFFFFF !important;
    text-shadow: 1px 0px purple;
}

#index_stats
{
    margin-right: -295px !important;
}

.unread .col_c_icon img, .notnew .col_c_icon img , .redirect_forum .col_c_icon img, #tracker_project_listing .col_c_icon img
{
    visibility: hidden !important;
}

.unread .col_c_icon, .notnew .col_c_icon, redirect_forum .col_c_icon, #tracker_project_listing .col_c_icon{
    height: 0 !important;
	width: 0 !important;
	padding-right: 30px !important;
	padding-top: 30px !important;
}

.unread .col_c_icon
{
   
	background: url(http://i1071.photobucket.com/albums/u511/Joske_wyvern/heart.png) no-repeat !important;
    background-position: center !important;
}

.notnew .col_c_icon, #tracker_project_listing .col_c_icon
{
	background: url(http://i1071.photobucket.com/albums/u511/Joske_wyvern/heartRead.png) no-repeat !important;
    background-position: center !important;
}

.redirect_forum .col_c_icon
{
	background: url(http://fearless-assassins.com/public/style_images/elegant/f_redirect.png) no-repeat !important;
    background-position: center !important;
}

#ips_addEventButton, .topic_buttons a, .active,.post_controls  a, .input_submit
{
    background: #FF5CAB !important;
    
}



}
