css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("review.cyanogenmod.com"),
domain("r.cyanogenmod.com") {

body {
    color: #fff !important;
}

body,
td,
#gerrit_body,
#gerrit_header,
#gerrit_topmenu {
    background: #000 !important;
}

.gwt-Label,
#gerrit_body tr > td:not(:last-child),
#gerrit_body.patch tr > td,
#gerrit_body tr > td div {
    color: #fff !important;
}

#gerrit_topmenu tbody tr td table.gwt-TabBar {
    border: 0 !important;
}

}
