css:
@-moz-document url-prefix(http://www2.kartellwar.de/community/) {
	.forumrow {
		background-color: #000 !important;
	}
	.threadinfo, .threadstats, .threadlastpost, .above_threadlist_controls, .thread_controls, .blockrow, .wgo_subblock {
		background-color: #000 !important;
	}
	.postdetails, .postrow, .userinfo {
	    background-color: #000 !important;
	    color:#999 !important;
	}
	a.username > strong > span, a.username > span {
	   color:#BBB !important;
	}
	
	div.wgo_subblock {
	   color:#DDD !important;
	}
	
	.blocksubhead {
	   color:#BBB !important;
	}
	
	.navlinks, .postbody {
	   background-color:#333 !important;
	}
	
	#sidebar {
	   background-color:#222 !important;
	}
	
	.blockbody {
	   background-color:#222 !important;
	   color:#999 !important;
	}
}
