css:
@-moz-document url-prefix("https://apps.facebook.com/thesimssocial/") {
html#facebook,
body,
body>div._li,
div#content,
div#globalContainer,
div#content,
div#content>div,
div#mainContainer,
div#contentCol,
div#contentArea,
div#pagelet_canvas_content,
div#app_content_144959615576466,
div#pagelet_iframe_canvas_content,
div#pagelet_iframe_canvas_content>div,
iframe#iframe_canvas,
div#mainContainer>div#contentCol>div#contentArea,
body>div._li>div#globalContainer
{
 width: 100% !important;
 height: 100% !important;
 margin: 0px !important;
 padding: 0px !important;
 border: 0px none !important;
 position: static !important;
 min-width: 0px !important;
 min-height: 0px !important;
}



div#rightCol,
#leftColContainer,
#toolbarContainer
{
 display: none;

}

div#contentArea
{
 display: block !important;
}
dis_iframe#iframe_canvas
{
 width: 800px !important;
 height: 800px !important;
}

#pagelet_bluebar
{
 display:none;
}
}

@-moz-document url-prefix("https://simssoc.game.playfish.com/g/fb/simssoc/") {
html, 
body#canvasPage,
div#content,
div#console_container,
div#leaderboard,
div#game_container,
div#flashcontent,
object#simssoc
{
 width: 100% !important;
 height: 100% !important;
 margin: 0px !important;
 padding: 0px !important;
 border: 0px none !important;
}

object#simssoc
{
 display:block;
}


div#console_container>div,
div#menu_bar_buttons>a
{
 display: none !important;
}

div#console_container>div#menu_bar,
div#console_container>div#game_container
{
 display: block !important;
}

div#menu_bar
{
 height: 0px !important;
 position: absolute;
}

div#game_counter_thesimssocial
{
 position: absolute !important;
 left: 0px !important;
 top: 0px !important;
 right: auto !important;
 z-index: 100;
 margin: 0px !important;
}
div#game_counter_thesimssocial>div.game_counter_value
{
 position: static !important;
}
}
