css:
@-moz-document url-prefix("http://www.tumblr.com"), url-prefix('https://www.tumblr.com')   {

body
{
background:url(http://i.imgur.com/b1KMG.jpg);
background-attachment:fixed;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
background:rgba(0, 128, 255, 0.37);
}

div.l-header .selection_nipple
{
border-bottom:12px solid #0080FF;
}

#posts .post blockquote
{
border-left:solid 4px #00D6FF;
}

#posts .post blockquote blockquote
{
border-left:solid 4px #00C6EC;
}

#posts .post blockquote blockquote blockquote
{
border-left:solid 4px #00ABCC;
}

#posts .post blockquote blockquote blockquote blockquote
{
border-left:solid 4px #0087A2;
}

#posts .post blockquote blockquote blockquote blockquote
{
border-left:solid 4px #026B80;
}

#posts .post .post_info a
{
color:#0000FA;
}

.new_post_label .new_post_label_icon
{
background-image:url(http://i.imgur.com/PuAkt.png);
}

#logo
{
background-image:url(http://i.imgur.com/T0Ooo.png);
}

#dashboard_controls_open_blog #open_blog_link
{
color:white;
}

}
