css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("select.yeeyan.org") {
.s_hearder,.s_wrapper{
box-shadow:none!important;
}
.s_wrapper{
border-left: 2px solid rgb(247,247,247) !important;
border-right: 2px solid rgb(247,247,247) !important;
}
.s_main{
border: none !important;
}
.s_title span{
height:16px!important;
padding:8px 8px 5px 15px!important;


}
.sa_right .s_title{
border: none !important;
}
.sa_articleinfo{
box-shadow:none !important;
background-color:rgb(247,247,247) !important;
}
.s_right_top{
box-shadow:none !important;
background:rgb(247,247,247) !important;
border: none !important;
}
.s_top_tag{
border: none !important;
}
.s_top_tag .active{
background:none!important;
border-bottom: 2px solid #2EABFD !important;
}
.s_top_tag a{
padding:0 10px 5px!important;
}
.sa_abstract{
border-color: #CACACA #CACACA #CACACA #2EABFD!important;
}
.blue_btn{
background:none!important;
border: 1px solid rgb(227,227,227) !important;
color:rgb(45,45,45)!important;
}
.sa_content p{
color:rgb(65,65,65)!important;
font-family:黑体!important;
}

.fff_bgc,.sa_main .closetag{
background:rgb(247,247,247) !important;
}
}
