css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("disqus.com"),
    url-prefix("http://nikonrumors.com/")
{

  .time-ago, menu, .parent-link
  {
    transition: .2s linear;
    -moz-transition: .2s linear;
    -ms-transition: .2s linear;
    -webkit-transition: .2s linear;
    -o-transition: .2s linear;
  }

  .time-ago, .post-body header .bullet, .post-body footer menu, .parent-link
  {
    visibility: hidden;
    opacity: 0;
    -moz-opacity:0;
    -khtml-opacity:0;
  }
  
  .post-body:hover .time-ago
  {
     visibility: visible;
    opacity: 1;
    -moz-opacity:1;
    -khtml-opacity:1;
  }

  .post-body:hover header .bullet
  {
     visibility: visible;
    opacity: 1;
    -moz-opacity:1;
    -khtml-opacity:1;
  }

  .post-body:hover footer menu
  {
     visibility: visible;
    opacity: 1;
    -moz-opacity:1;
    -khtml-opacity:1;
  }

  .post-body:hover .parent-link
  {
     visibility: visible;
    opacity: 1;
    -moz-opacity:1;
    -khtml-opacity:1;
  }
  
}
