css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("springpad.com") {

/* Remove top padding of notebook list */
section.home-panel ul.notebooks-list {
    padding-top: 0px !important;
}

/* Remove the user image */
section.home-panel a.user-photo-link {
    display: none !important;
}

/* Make the notebooks smaller */
section.home-panel section.nb-item {
    max-height: 230px !important;
    width: 160px !important;
}

section.home-panel ul.notebooks-list section.nb-item header {
    height: 90px !important;
    top: 70px !important;
}

section.home-panel ul.notebooks-list.-condensed section.nb-item header {
    height: 90px !important;
    top: 0px !important;
}

section.home-panel ul.notebooks-list li.search-result {
    padding: 0px 10px 20px 10px !important;
}

/* Remove the lock icon and item count on notebooks */
section.home-panel section.nb-item aside {
    /*width: 45px !important;*/
    display: none !important;
}

/* Remove Category/Tag link on notebooks */
section.home-panel section.nb-item ul.nb-tags {
    display: none !important;
}

/* Remove the members list on notebooks */
section.home-panel section.nb-item ul.nb-users {
    display: none !important;
}

section.home-panel section.user-profile-page > section {
    padding-top: 10px !important;
}

/* Make notebook title text wrap again */
section.home-panel span.nb-title {
    overflow: visible !important;
    text-overflow: ellipsis !important;
    white-space: normal !important;
}

/* Make notebooks float :) */
section.home-panel > section > section > section {
    width: 100% !important;
}


}
