css:
@-moz-document domain("wikibooks.org"), domain("wikipedia.org"), domain("wikiquote.org"), domain("wikisource.org"), domain("wikiversity.org"), domain("wiktionary.org"), domain("wikimedia.org") {

#mw-head-base { height: 5em !important; }

#siteNotice, #p-moodbar, #mw-panel, #mw-dismissable-notice, #p-cactions, #p-views, .feedbackWrapper, #mw-articlefeedback, #mw-articlefeedbackv5, #footer { display: none !important; }

}


@-moz-document domain("en.wikibooks.org"), domain("en.wikipedia.org"), domain("en.wikiquote.org"), domain("en.wikisource.org"), domain("en.wikiversity.org"), domain("en.wiktionary.org"), domain("en.wikimedia.org") {
#left-navigation { left: 0 !important; margin-left: 0 !important; }
#mw-head-base { margin-left: 0 !important; }
#content { margin-left: 0 !important; }
}


@-moz-document domain("he.wikibooks.org"), domain("he.wikipedia.org"), domain("he.wikiquote.org"), domain("he.wikisource.org"), domain("he.wikiversity.org"), domain("he.wiktionary.org"), domain("he.wikimedia.org") {
#left-navigation { right: 0 !important; margin-right: 0 !important; }
#mw-head-base { margin-right: 0 !important; }
#content { margin-right: 0 !important; }
}
