css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("anna-news.info") {
@media screen {
div.comment>div.content, div.node>div.content{
column-count: auto;
column-width: 350px;
column-gap: 3em;
column-rule: 1px dashed black;
    -moz-column-count: auto;
    -moz-column-width: 320px;
    -moz-column-gap: 3em;
    -moz-column-rule: 1px dashed black;
    -webkit-column-count: auto;
    -webkit-column-width:350px;
    -webkit-column-gap: 3em;
    -webkit-column-rule: 1px dashed black;
text-align: justify;
margin: 1em !important;
}
}

iframe {
width: 350px !important;
}

.pager-current {
color: red;
}

:visited {
color: red;
}

div[class^="node"]>div.content, div[class^="node"]>div.content>object, div[class^="node"]>div.content>object>embed {
 width: 99% !important;
 height: auto !important;
}

div#player>div.player, div#player>div.player-container, div#player>div.player-container>embed{
 width: 99% !important;
 height: auto !important;
}

div.links>ul{
color: red !important;
display: inline;

}

div.node_images img{
border: 4px double black;
text-align:center !important;
}

div.content img{
width: auto;
max-width: 100%;
}

div.node_images{
    text-align:center !important;
}

div.comment>h3{
display: none;
}

div.comment>div.submitted{
background-color: #9ACABA;
border-radius: 5px;
color: black !important;
}

div.breadcrumb{
display: none;
}

div#main{
padding: 2px !important;
}

div.node>h2{
margin: 8px !important;
padding: 0 0 2px 5px !important;
}

div.node>span.submitted{
margin: 0 0 0 18px !important;
}
div#main>div.node{
margin: 3px !important;
}

div.node>span.taxonomy, div.node>span.submitted{
margin: 0 0 0 10px !important;
display: inline-block !important;
}

div.views-row div.content{
margin: 0 10px 0 10px !important;
text-align: justify;
}

li.node_read_more{
display: none !important;
}

div.comment{
border-radius: 10px;
}

div.indented{
margin-left: 90px !important;
margin-top: -40px !important;
background-color: rgb(255,255,255) !important;
}

div.indented div.submitted{
background-color: #CAFAEA;
}

div#main>div.node>div.links{
display: inline !important;
}

div.google-addone{
display: inline !important;
}

div#block-block-3>div.content, div#block-block-3>h2{
opacity:0;
}

div#block-block-3>div.content:hover{
transition-property: opacity;
transition-duration: 2s;
opacity: 0.5;
}

div#block-block-3{
margin-left:auto;
margin-right:auto;
width: 98%;
height: 196px;
background-image: url(http://historic.ru/maps/item/f00/s00/z0000005/pic/index.gif) !important;
background-size: contain;
border-radius: 5px;
border: 3px solid #9ACABA;
}

h1.title{
display: none;
}

div.breadcrumb + h1.title{
display: block;
}

.sticky {
    background-color: rgba(255,255,255,0) !important;
    border: 1px solid rgba(255,255,255,0) !important;
}



@media print{
*{
    background-color: rgb(255, 255, 255) !important;
    color: black !important;
}

div.google-addone, div#block-sape-1, body>table#_header, body>table#footer, td#sidebar-left, td#sidebar-right{
display: none !important;
}

div.links{
display: none !important;
visibility: hidden !important;
}


div.comment>div.content, div.node>div.content{
column-count: auto;
column-width: 300px;
column-gap: 3em;
column-rule: 1px dashed black;
    -moz-column-count: auto;
    -moz-column-width: 300px;
    -moz-column-gap: 3em;
    -moz-column-rule: 1px dashed black;
    -webkit-column-count: auto;
    -webkit-column-width:300px;
    -webkit-column-gap: 3em;
    -webkit-column-rule: 1px dashed black;
text-align: justify;
margin: 1em !important;
width: 100% !important;
}

img{
display: none;
}

}

}
