css:
@-moz-document domain("www.reddit.com") {
.comment>.entry>div>.buttons li + li
{
    padding-left: 0;
}
.comment>.entry>div>.buttons li
{
    padding-right: 0;
}
.comment>.entry>div>.buttons li>*
{
    display: inline-block;
}
.comment>.entry>div>.buttons li>*
{
    margin-right: 4px;
}
.comment>.entry>div>.buttons li + li>*
{
    margin-left: 4px;
}
.comment>.entry>div>.buttons>li>.give-gold
{
    display: none;
}
}
