@-moz-document domain("softpedia.com") {
* { color: #999 !important; background: #111 !important; border-color: #333 !important; text-shadow: none !important; }
a, a * { color: #f60 !important; text-decoration: none !important; }
a:visited, a:visited * { color: #f80 !important; }
a:hover, a:hover * { color: #f00 !important; }
}
