css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("^https?://(www\.)?google\.(com?\.)?[a-z]{2,3}/.*$") {
    
    body {
        position: relative;
    }
    
    #mngb {
        position: fixed;
        width: 100%;
        z-index: 100;
    }
    
    #main {
        position: relative;
        top: 105px;
        z-index: 20;
    }
    
    #pocs,
    body > table {
        position: fixed !important;
    }
    
}
