/* about.com My Forums Readability Version 0.2 */

/* Improves layout and readability of the Forum Settings page */
/* Now uses black text instead of grey on other forum pages, too. */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.about.com") {

table#pfMyForums.ptcContent a {
font-weight:bold !important;
}

table#pfMyForums.ptcContent th.ptcName,
table#pfMyForums.ptcContent td.ptcValue,
table#pfMyForums.ptcContent td.ptcAltValue,
table#pfMyForums.ptcContent a.navLink,
table#pfMyForums.ptcContent td.ptcName,
span.ptcListHeader,
table.ptcContentTable caption,
div.ptcHeaderText,
td.ptHeaderText,
div.ptBlueBody td,
span.ptcNameCell,
a.ptcMemberLink,
span.ptcValueCell,
td.ptcDiscColPosts.ptcValueCell,
td.ptcDiscColLatest.ptcValueCell
{
color:black !important;
}

table#pfMyForums.ptcContent a.navLink {
font-size:1em !important;
}

table#pfMyForums.ptcContent th.ptcName {
text-align:left !important;
}

table#pfMyForums.ptcContent td.ptcHead {
padding-bottom:10px !important;
padding-top:10px !important;
background-color:#E8E8E8 !important;
}

}
