/* about.com Editable Shading Version 0.1 */

/* Replaces the weak headline shading of
   various elements with a darker color  */


@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.about.com") {

td.pfFolder, td.pfEmptyFolder, table.pfEmptyFolder,
div.ptcSearch, table.ptcContentTable caption, 
div.ptcForumFoldersHeader div.ptcHeadertext,
div.ptForumFoldersHeader td.ptHeadertext,
div#PTID-discussionList-footer td.ptcListContainerCellLeft,
div#PTID-messageList-header td.ptcTableContainerCell td.ptcListContainerCellLeft,
div#PTID-messageList-header td.ptcTableContainerCell td.ptcListContainerCellRight,
div#PTID-messageList-header td.ptcTableContainerCell td.ptcListContainerCell
 {
background-image:none !important;
background-color:#E8E8E8 !important;
}

}
