css:
#docs-side-toolbar
{
    display: none;
}
#docs-toolbar-wrapper,
#docs-primary-toolbars {
    max-width: 100% !important;
    width: 100% !important;
    padding: 0 5px !important;
}

/* calming colors */
.kix-appview-editor,
.kix-page,
.kix-paginateddocumentplugin > div
{
    background: #e0e5e5 !important;
}
.kix-paginateddocumentplugin,
.kix-paginateddocumentplugin-compact-mode
{
    border: 0 !important;
    box-shadow: 0 0 0 0 !important;
}
.kix-page
{
    border: 0 !important;
    margin-top: 0 !important;
}

/* table borders colors in the document */
.kix-paginateddocumentplugin-compact-mode tbody td
{
    border: 1px solid #d0d5d5!important;
}

/* replace toolbar shadow */
.kix-document-top-shadow-inner
{
    display: none;
}
#docs-toolbar-wrapper
{
    border-bottom: 1px solid darkgrey !important;
}
