css:
@-moz-document domain("sapphire.cc") {

body{
background: #BABABA !important;
color: black !important;
text-shadow: 0 1px white !important;
}

td, tr, font, table{
font-size: 12px !important;
}

a{
color: black !important;
text-shadow: 0 1px white !important;
}

table{
background: white !important;
}

#menu, #main_menu li, #main_menu li a{
background: gray !important;
color: white !important;
text-shadow: 0 1px black !important
}

#menu a, .index a{
color: white !important;
text-shadow: 0 1px black !important;
}

td, tr, table{
color: black !important;
text-shadow: 0 1px white !important;
}

.colhead{
background: white !important;
}

table, table.futoszoveg, td.kereso, td.kereso2, table.dropdown,  td, #main_menu li{
-webkit-box-shadow:0 8px 6px -6px #D9D9D9!important;
-moz-box-shadow:0 8px 6px -6px #D9D9D9!important;
box-shadow:0 8px 6px -6px #D9D9D9!important;
}

td{border-color: #B3B3B3 !important;}

input, select, textarea{
border-radius: 4px !important;
background: #ededed !important;
border: 1px solid #c2c2c2 !important;
padding: 3px !important;
color: black !important;
}

font[color="#5FFB17"], font[color="lime"], font[color="#FBB117"], font[color="#ffb90f"]{
color: green !important;
}

font[color="#FFCC11"], font[color="#E3E3E3"], font[color="#ffffff"], span[class="date"], font[color="#1e1e1e#000000"], font[color="#1e1e1e#550000"]{
color: black !important;
}

div[class="mCustomScrollBox"], div[class="mCustomScrollBox"], div[class="mCustomScrollBox"] a{
color: black !important;
text-shadow: none !important;
}

}
