css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.reddit.com"), domain("reddit.com") {
    .sitetable .arrow, body:not(.subscriber) .arrow, #siteTable .arrow.down, #siteTable .arrow.up, .link .arrow.down, .link .arrow.up, .link .arrow.downmod, .link .arrow.upmod {
        display: block !important;
        visibility: visible !important;
    }
    .link .midcol {
        display: block !important;
        visibility: visible !important;
        height: auto !important;
    }
    .score {
        visibility: visible !important;
    }
    .toggle.report-button {
        display: inline !important;
        visibility: visible !important;
    }
}
