css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://loveplanet.ru/a-iminit") {
    .msg_col_left, ul.comm_format_panel, #modal {display: none !important}
    
    body * {
        border-radius: 0.5em;
    }
    
    body, #mes, .grey_box,.msg_win_cont_top,.msg_win_cont_btm,
    .msg_win_folder_h,.msg_win_tab li, #histbar a{
        background-color: hsla(60,100%,70%,0.3) !important;
    }
    
    .msg_win_flist_add,.noread,.comm_add_box,.msg_win_tab li.current_clr {
        background-color: hsla(60,80%,50%,0.5) !important;
    }
    
    .ltgrey{
        background-color: hsla(60,100%,80%,0.3) !important;
        color: hsl(60,100%,35%) !important;
    }
    
    b.darkblue{
        color: hsl(80,100%,20%) !important;
    }
    
    .corr1{
        font-family: Verdana !important;
        font-style: italic !important;
        color: hsl(240,100%,20%) !important;
    }
    
    .corr2{
        font-family: Verdana !important;
        font-style: italic !important;
        color: hsl(90,100%,20%) !important;
    }
        
    .msg_win_box{
        border: 1px solid hsl(60,100%,35%) !important;
    }
    
    .fr.msg_win_submenu{
        font-weight: bold !important;
        position:absolute !important;
        top: 107px  !important;
        left: 300px  !important;
    }
    
    .msg_wrap {
        width: 800px !important;
    }
}
