@-moz-document domain("addictinggames.com") {
::-webkit-scrollbar-thumb,
body::-webkit-scrollbar-thumb,
ol::-webkit-scrollbar-thumb,
ul::-webkit-scrollbar-thumb,
#transcript-scrollbox::-webkit-scrollbar-thumb {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border-radius: 22px!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar-thumb:hover,
body::-webkit-scrollbar-thumb:hover,
ol::-webkit-scrollbar-thumb:hover,
ul::-webkit-scrollbar-thumb:hover,
#transcript-scrollbox::-webkit-scrollbar-thumb:hover {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-track,
#messages div::-webkit-scrollbar-track {
    /* Color of slider */
    background: #170000!important;
    box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar,
#messages div::-webkit-scrollbar {
    /* Width */
    width: .8em!important;
}
 
::-webkit-scrollbar-button,
#messages div::-webkit-scrollbar-button {
    /* Color of handles at top and bottom */
    background: #4c0707!important;
    box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-button:hover,
#messages div::-webkit-scrollbar-button:hover {
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
}

@-moz-document domain("addictinggames.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

selector {
	text-transform: capitalize!important;
}

selector,
:focus {
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

/*___ Fonts ________________________________________________________________________*/

.authr-boxs h6,
.authr-boxs span,
.foot-box ul li a,
.foot-menu ul li a,
.foot-sec h4,
.inn_panel1_box .txt_box p,
.instru-blk p,
.new-carao .caro-itm .media a .media-body,
.recent-revw-sec .box p,
.top-head h1,
.top-head h2,
.top-head h3,
.top-head h4,
.txt_box h4 a,
body
{
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

selector {
	font-size: 21px!important;
}

.txt_box h4 a {
	font-size: 19px!important;
}

selector {
	font-size: 17px!important;
}

.inn_panel1_box .txt_box p,
.new-carao .caro-itm .media a .media-body
{
	font-size: 15px!important;
}

selector {
	font-size: 14px!important;
	line-height: 19px!important;
}

/*___ Remove Items _________________________________________________________________*/

#ad_main_leader {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

#main {
    margin-top: 3rem!important;
}

/*___ Margins ______________________________________________________________________*/

.addiction-game-sec,
.blck-secgame .game-gallerty .inn_box_panel1,
.category_games .inn_box_panel1,
.category_games1 .game-gallerty .inn_box_panel1,
.game-gallerty .inn_box_panel1,
.recent-games .inn_box_panel1,
.recent-revw-sec .col-lg-3
{
	margin: 12px!important;
}

selector {
	margin: 3rem!important;
}

.recent-game-reviews>.row .box {
    margin-top: 0!important;
}

/*___ Paddings _____________________________________________________________________*/

.addiction-game-sec,
.blck-secgame .game-gallerty .inn_box_panel1,
.category_games .inn_box_panel1,
.category_games1 .game-gallerty .inn_box_panel1,
.game-gallerty .inn_box_panel1,
.new-carao .caro-itm,
.page-user .profile-form form,
.recent-games .inn_box_panel1,
.recent-revw-sec .col-lg-3
{
	padding: 12px!important;
}

selector {
	padding: 6px!important;
}

/*___ Heights ______________________________________________________________________*/

selector {
	max-height: none!important;
}

selector {
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

body {
	max-width: none!important;
}

selector {
	width: auto!important;
}

.addiction-game-sec>.container,
.category_games .container,
.category_games1 .container,
.recent-games .container
{
	width: 98%!important;
}

.recent-revw-sec .col-lg-3 {
    width: 22.9%!important;
}







}

@-moz-document domain("addictinggames.com") {
/*__________________________________________________________________________________*/
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

div.avatar-selection-pager-nav .pager-current,
div.avatar-selection-pager-nav a:hover,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.bg-white,
.inn_panel1_box .txt_box p,
.modal-dialog .modal-content .modal-body,
li,
p
{
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

.h1.larger,
.top-head h1,
.top-head h2,
.top-head h3,
.top-head h4,
h1,
h1.larger,
h2
{
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/


.top-head h1 span,
.top-head h3 span,
.top-head h4 span,
h3,
h4
{
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

.page-user .profile .profile-content dt,
.authr-boxs span,
.gameInfo dt,
h5,
h6,
label {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

.page-user .profile .profile-content dd,
time {
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/

.favoriteGameAction.h3 {
	color: #07ff24!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/

selector {
	color: #ff0707!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

.navTabs>.selected a {
    color: #f6f55f!important;
}

div.error, .error {
    color: #ffe4e4!important;
}

div.status, .ok {
    color: #e6f8d3!important;
}











}

@-moz-document domain("addictinggames.com") {
/*__________________________________________________________________________________*/

body,
html {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

/*___ Background Gray ______________________________________________________________*/


.head_sec,
.foot-sec {
	background-color: #060505!important;
}

.breadcrumb,
.top_panel_sec {
    background: #0a0808!important;
}

/*___ Background 0000 ______________________________________________________________*/

#main,
.blck-sec,
.gamepg-pnl,
.inn_box_panel1_box,
.inn_panel1_box .txt_box h4,
.inn_panel1_box .txt_box p,
.recent-revw-sec .box,
.search_box,
.taxonomy-description,
.txt_box,
div.avatar-selection-pager-nav .pager-current,
div.avatar-selection-pager-nav a,
div.avatar-selection-pager-nav a:hover
{
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ________________________________________________________*/


selector {
	background-color: rgba(0, 0, 0, 0)!important;
}

/*__ Blue __________________________________________________________________________*/

.rt_buttins ul li:last-child a,
.vie-cata {
	background-image: linear-gradient(to bottom right, #043dda, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.rt_buttins ul li:last-child a:hover,
.vie-cata:hover {
	background-image: linear-gradient(to bottom right, #154eeb, #000)!important;
	color: #ff0!important;
}

.rt_buttins ul li:last-child a:active,
.vie-cata:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Red __________________________________________________________________________*/

.hot-games .top-head .top-head-rt .fltr .form-type-select.form-item-sort-by .bef-select-as-links>.form-item>.form-item a.active,
.page-user .profile-form .form-actions.form-wrapper #edit-submit--3,
.rt_buttins ul li:nth-last-child(2) a,
form#user-register-form>div .form-actions input
{
	background-image: linear-gradient(to bottom right, #f00, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.hot-games .top-head .top-head-rt .fltr .form-type-select.form-item-sort-by .bef-select-as-links>.form-item>.form-item a.active:hover,
.page-user .profile-form .form-actions.form-wrapper #edit-submit--3:hover,
.rt_buttins ul li:nth-last-child(2) a:hover,
form#user-register-form>div .form-actions input:hover
{
	background-image: linear-gradient(to bottom right, #f11, #000)!important;
	color: #ff0!important;
}

.hot-games .top-head .top-head-rt .fltr .form-type-select.form-item-sort-by .bef-select-as-links>.form-item>.form-item a.active:active,
.page-user .profile-form .form-actions.form-wrapper #edit-submit--3:active,
.rt_buttins ul li:nth-last-child(2) a:active,
form#user-register-form>div .form-actions input:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Orange ________________________________________________________________________*/

selector {
	background-image: linear-gradient(to bottom right, #da3a04, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #eb4b15, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Gray ________________________________________________________________________*/

.hot-games .top-head .top-head-rt .fltr .form-type-select.form-item-sort-by .bef-select-as-links>.form-item>.form-item,
.more-categories,
.new-carao .caro-itm {
	background-image: linear-gradient(to bottom right, #191a18, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.hot-games .top-head .top-head-rt .fltr .form-type-select.form-item-sort-by .bef-select-as-links>.form-item>.form-item:hover,
.more-categories:hover,
.new-carao .caro-itm:hover {
	background-image: linear-gradient(to bottom right, #2a2b29, #000)!important;
	color: #ff0!important;
}

.hot-games .top-head .top-head-rt .fltr .form-type-select.form-item-sort-by .bef-select-as-links>.form-item>.form-item:active,
.more-categories:active,
.new-carao .caro-itm:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*___ Plain Black __________________________________________________________________*/

.addiction-game-sec:not(.new-game),
.category_games1 .addiction-game-sec,
.gamepg-pnl>.container>.row>.col-sm-2,
.instru-blk,
.page-user .profile-form form,
.playlist-game__slider,
.recent-revw-sec
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.addiction-game-sec:not(.new-game):hover,
.category_games1 .addiction-game-sec:hover,
.gamepg-pnl>.container>.row>.col-sm-2:hover,
.instru-blk:hover,
.page-user .profile-form form:hover,
.playlist-game__slider:hover,
.recent-revw-sec:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Plain ________________________________________________________________________*/

.recent-revw-sec .col-lg-3,
.blck-secgame .game-gallerty .inn_box_panel1,
.category_games .inn_box_panel1,
.category_games1 .game-gallerty .inn_box_panel1,
.game-gallerty .inn_box_panel1,
.recent-games .inn_box_panel1
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

.recent-revw-sec .col-lg-3:hover,
.blck-secgame .game-gallerty .inn_box_panel1:hover,
.category_games .inn_box_panel1:hover,
.category_games1 .game-gallerty .inn_box_panel1:hover,
.game-gallerty .inn_box_panel1:hover,
.recent-games .inn_box_panel1:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}

/*___ Red Gradient _________________________________________________________________*/

div.error, table tr.error {
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Blue Gradient _________________________________________________________________*/

selector {
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/*__ Green Gradient ________________________________________________________________*/

div.status, table tr.ok {
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Yellow Gradient _______________________________________________________________*/

selector {
	background: linear-gradient(to bottom, #9d9d5d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}

/*__ Orange Gradient _______________________________________________________________*/

selector {
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}








}

@-moz-document domain("addictinggames.com") {
/*__________________________________________________________________________________*/

#topGamesContent .gameSlot,
.abs_button,
.addiction-game-sec,
.blck-secgame .game-gallerty .inn_box_panel1,
.category_games .inn_box_panel1,
.category_games1 .game-gallerty .inn_box_panel1,
.col-md-i2v-2,
.col-sm-12,
.col-sm-6,
.game-gallerty .inn_box_panel1,
.inn_box_panel1_box,
.layout-container--sidebar .owl-item,
.new-carao .caro-itm,
.recent-games .inn_box_panel1
{
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

#topGamesContent .gameSlot:hover,
.abs_button:hover,
.addiction-game-sec:hover,
.blck-secgame .game-gallerty .inn_box_panel1:hover,
.category_games .inn_box_panel1:hover,
.category_games1 .game-gallerty .inn_box_panel1:hover,
.col-md-i2v-2:hover,
.col-sm-12:hover,
.col-sm-6:hover,
.game-gallerty .inn_box_panel1:hover,
.inn_box_panel1_box:hover,
.layout-container--sidebar .owl-item:hover,
.new-carao .caro-itm:hover,
.recent-games .inn_box_panel1:hover
{
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

img {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/

p,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

#topGamesContent .gameSlot:hover p,
.category_games1 .game-gallerty .inn_box_panel1:hover p,
.col-md-i2v-2:hover p,
.game-gallerty .inn_box_panel1:hover p,
p:hover,
selector:hover p,
tr:hover
{
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}
}
