@-moz-document domain("rottentomatoes.com") {
/*________________________________________________________________________*/
* {
	border: none!important;
	border: 0!important;
	box-shadow: none!important;
	outline: none!important;
	text-decoration: none!important;
}

table {
	border-collapse: separate!important;
	border-spacing: 9px!important;
}

.carousel .item .day-of-month,
.carousel .item .day-of-week,
.initialism,
.movie_list tr .right_col,
.navbar.navbar-rt #menu .menuHeader > a,
.text-uppercase,
h1,
h2,
h3 {
	text-transform: capitalize!important;
}


/*___ Fonts ________________________________________________________________*/
#banner .title,
#foot .subnav h2,
#foot a,
#foot div,
#header-main .dropdown-menu h2,
#header-main .dropdown-menu li a,
#headliners h3,
#homepage-spotlight1 h3,
#homepage-spotlight2 h3,
#listColumn .checkbox .movie-list-checkbox,
#listColumn .checkbox .theater-list-checkbox,
#location-picker,
#mainColumn .showtimes-body .showtimes-presentations .presentation .presentation-name,
#mainColumn .showtimes-body .showtimes-presentations .presentation .trait,
#miniMOB h2,
#miniMOB,
#movieMenu,
#newsAndFeaturesWrap,
#newsMenu,
#showtimes .showtimesContainer .showtimes-presentations .presentation .presentation-name,
#showtimes .showtimesContainer .showtimes-presentations .presentation .trait,
#ticketingMenu,
#topSection .pull-right.hidden-xs,
#tvMenu,
.articleContentBody p,
.articleLink,
.btn,
.carousel .item .day-of-month,
.carousel .item .day-of-week,
.celeb_name h1,
.cfpItem p.title.noSpacing,
.cfpModule .col-sm-6,
.clickForMore a,
.critic-name,
.fgm,
.home-news-article-link,
.initialism,
.input-group,
.meter-value,
.meter.media,
.modal-content,
.movie_list tr .middle_col,
.movie_list,
.panel-rt .panel-heading,
.panel-rt.panel .panel-heading,
.quickLink,
.season_info .subtle.noSpacing,
.seasonItem .bold,
.subtle,
.subtle.bold,
.text-uppercase,
.theater-heading address,
.theater-heading label,
.theater-heading p,
.theater-heading span,
.tMeterScore,
.videoItem .detail,
.videoItem .title,
p,
li,
table,
body,
h1.panel-heading,
h3,
html,
label,
span.bold {
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

#mainColumn .showtimes-body .movie-title,
.poster_carousel .poster_caption a.title {
	font-size: 30px!important;
}

body,
.movie_list .tMeterScore {
	font-size: 24px!important;
	line-height: 27px!important;
}

.videoItem .title {
	font-size: 23px!important;
}

#mainColumn .showtimes-body .showtimes-presentations .presentation .purchase-buttons .btn,
#showtimes .showtimesContainer .showtimes-presentations .presentation .purchase-buttons .btn,
.home-news-article-link,
.videoSection .panel-heading {
	font-size: 20px!important;
}

#listColumn .list-title,
#mainColumn .showtimes-body .showtimes-presentations .presentation .presentation-name,
#mainColumn .showtimes-body .showtimes-presentations .presentation .trait,
#showtimes .showtimesContainer .showtimes-presentations .presentation .presentation-name,
#showtimes .showtimesContainer .showtimes-presentations .presentation .trait,
.cfpItem p,
.home-news-source,
.mb-movies .mb-movie .movie_info .movieTitle,
.navbar.navbar-rt #menu #header-news-best-worst .newsTitle,
.navbar.navbar-rt #menu #header-news-guides .newsTitle,
.navbar.navbar-rt #menu #header-news-rtnews .newsTitle,
.navbar.navbar-rt #menu #tvMenuDropdown .newsTitle,
.subtle,
.theater-heading address,
.theater-heading label,
.theater-heading p,
.theater-heading span,
.videoItem .detail {
	font-size: 18px!important;
}

#listColumn label,
.tMeterIcon.tiny.noRating {
	font-size: 15px!important;
}

.navbar.navbar-rt #menu #header-news-guides .newsTitle,
.mb-movies .mb-movie .movie_info .movieTitle,
.navbar.navbar-rt #menu #header-news-rtnews .newsTitle,
.navbar.navbar-rt #menu #header-news-best-worst .newsTitle,
.navbar.navbar-rt #menu #tvMenuDropdown .newsTitle {
	line-height: 21px!important;
}


/*___ Remove Items _________________________________________________________*/
#miniMOB .adColumn,
.col-right,
.social-tools {
	position: absolute!important;
	top: -9999px!important;
	left: -9999px!important;
}


/*___ Left/Right/Top/Bottom __________________________________________________*/
.movie_list tr .middle_col {
	padding-left: 10px!important;
}

.freshPicks .cfpItem a .imgContainer img {
	left: 0px!important;
}

#headliners h3,
#homepage-spotlight1 h3,
#homepage-spotlight2 h3 {
	margin-bottom: 7px!important;
}


/*___ Margins ______________________________________________________________*/
.row {
	margin: 0 auto!important;
}

#boxoffice-list,
#content-column,
#opens-on-collection,
#other-list,
#theater-lists,
.col-sm-12,
.mb-movies .mb-movie,
.freshPicks .cfpItem,
.headline:not(:first-of-type),
.media-browser-left,
.home-body #videoPanel,
.movie-details-row,
.newsItem.col-sm-6,
.newsItem.col-sm-8.col-full-xs,
.panel {
	margin: 12px!important;
}

#mainColumn .showtimes-body .movie-title {
	margin-bottom: 23px!important;
}

.movie_list,
.videoItem .title,
[id*="homepage-"],
ul.redDot li {
	margin-bottom: 12px!important;
}

.tMeterIcon.tiny {
	padding-bottom: 1rem!important;
}

.header_main_scroll {
	margin-top: 6px!important;
}

.poster_carousel .poster_caption {
	bottom: -53px!important;
}


/*___ Paddings _____________________________________________________________*/
.articleContentBody,
#content-column,
.mb-movies .mb-movie,
.col-sm-12,
.freshPicks .cfpItem,
.movie-details-row,
.movie_list,
.newsItem.col-sm-6,
.newsItem.col-sm-8.col-full-xs,
[id*="homepage-"] {
	padding: 12px!important;
}

.countdown-index {
	padding: 10px 22px!important;
}

#videoPanel h3.panel-heading {
	padding: 3px 9px!important;
}


/*___ Heights _____________________________________________________________*/
.mb-movies:not(.list-view) .mb-movie .movie_info .movieTitle {
	Max-height: none!important;
}

.headline .headlineItem .bannerCaption,
.headline.headlineItem .bannerCaption,
.newsContainer .newsContainerItem,
.videoItem .bannerPic.headliner .videoPic,
.videoItem .bannerPic.headliner {
	height: auto!important;
}

.freshPicks .cfpItem a .imgContainer img {
	height: 100%!important;
}


/*___ Widths ______________________________________________________________*/
selector {
	max-width: none!important;
}

.freshPicks .cfpItem a .imgContainer img,
.freshPicks.inDropdownTv .cfpItem,
.tMeterIcon.tiny.noRating,
.videoItem .bannerPic .videoPic {
	width: auto!important;
}

.col-full,
.col-sm-17,
.container {
	width: 98%!important;
}

.navbar.navbar-rt #menu .menuHeader .dropdown-menu {
	width: 94%!important;
}

.col-sm-18 {
	width: 88%!important;
}

.col-left-center {
	width: 81%!important;
}

.col-center-right {
	width: 78%!important;
}

#mainColumn,
.col-xs-5.subnav.freshPicks.inDropdownTv {
	width: 75%!important;
}

#menu .list-inline {
	width: 64%!important;
}

.col-sm-11 {
	width: 60.833%!important;
}

.col-left-center .col-center {
	width: 58%!important;
}

.col-sm-12.leftColumn.col-full-xs {
	width: 51%!important;
}

.col-sm-8 {
	width: 50%!important;
}

.newsItem.col-sm-6 {
	width: 48% !important;
}

.col-sm-12 {
	width: 46%!important;
}

.freshPicks.inDropdown .cfpItem {
	width: 45%!important;
}

.col-left-center .col-left {
	width: 42%!important;
}

.col-xs-7 {
	width: 41.167%!important;
}

.col-sm-13 {
	width: 39.167%!important;
}

.col-sm-6 {
	width: 32%!important;
}

.newsItem.col-sm-8.col-full-xs {
	width: 31%!important;
}

#listColumn {
	width: 25%!important;
}

.freshPicks .cfpItem {
	width: 18%!important;
}

.movie_list tr .left_col {
	width: 17%!important;
}

.col-sm-6.col-full-xs,
.movie_list tr .right_col {
	width: 12%!important;
}

.countdown-index {
	width: 91px!important;
}
}

@-moz-document domain("rottentomatoes.com") {
/*________________________________________________________________________*/
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}


/*________________________________________________________________________*/
#movieMenu:hover,
#newsMenu:hover,
#ticketingMenu:hover,
#tvMenu:hover,
.countdown-item:hover .countdown-index,
a.articleLink:hover *,
a[href]:hover span,
a[href]:hover,
#browser-navigator .navigator-menu li.active a,
h1,
#browser-navigator .navigator-menu a:hover,
input[type="checkbox"]:checked + label,
input[type="radio"]:checked + label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}


/*________________________________________________________________________*/
#listColumn label,
#browser-navigator .navigator-menu li a,
#mobile-browser-navigator .navigator-menu li a,
.countdown-index,
li,
body,
.cfpItem p,
p {
	color: #fff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*__ Khaki ________________________________________________________________*/
#headliners h3,
#homepage-spotlight1 h3,
#homepage-spotlight2 h3,
#location-picker .text,
.carousel .item .day-of-week,
.editorial-panel-heading h1,
.FullscreenSearch__result-header,
.panel-heading,
.videoItem .title,
h1,
h2 {
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*__ Burlywood ____________________________________________________________*/
#listColumn .list-title,
.home-news-source,
.subtle,
.zipcode,
h3,
h4 {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*__ Sandy Brown __________________________________________________________*/
#banner .title em,
#mainColumn .showtimes-body .showtimes-presentations .presentation .presentation-name,
#mainColumn .showtimes-body .showtimes-presentations .presentation .trait,
#showtimes .showtimesContainer .showtimes-presentations .presentation .presentation-name,
#showtimes .showtimesContainer .showtimes-presentations .presentation .trait,
.articleContentBody .descriptor,
h5,
h6 {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*___ Blue ________________________________________________________________*/
.carousel .item .day-of-month,
.release-date,
.subtle.start-year {
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*___ Green _______________________________________________________________*/
selector {
	color: #0f0!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*___ Red _________________________________________________________________*/
.tMeterIcon.tiny.noRating {
	color: #f00!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*___ Black _________________________________________________________________*/
.videoSection .panel-heading {
	color: #000!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}


/*________________________________________________________________________*/
.a-input-text,
.btn-default,
input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}


/*________________________________________________________________________*/
::selection {
	background: #ff9!important;
	color: #000!important;
}

pre::selection {
	background: #ff9!important;
	color: #fff!important;
}
}

@-moz-document domain("rottentomatoes.com"), domain("d3biamo577v4eu.cloudfront.net") {
/*________________________________________________________________________*/
html,
body {
	background: #000 url(http://image.ibb.co/hYKqvw/noise_background.png)!important;
	background-attachment: fixed!important;
}

#banner .headlineItem .bannerCaption,
.headline .headlineItem .bannerCaption,
.headline.headlineItem .bannerCaption,
.trendingBar + .banner .headlineItem .bannerCaption {
	background-color: #000!important;
}


/*___ Background Gray ______________________________________________________*/
#foot div {
	background-color: #060505!important;
}


/*___ Background 0000 ______________________________________________________*/
#topbar-container .topbar,
.home-body #videoPanel {
	background: rgba(0, 0, 0, 0)!important;
}


/*___ Background-color 0000 _________________________________________________*/
#foot,
#mainColumn,
.poster_carousel .poster_caption,
.container,
.editorial-panel-heading h1,
.FullscreenSearch__result-header,
.navbar.navbar-rt #menu .menuHeader #ticketingMenu:hover,
.navbar.navbar-rt #menu .menuHeader:hover,
.panel,
.panel-heading,
.sleaderboard_wrapper {
	background-color: rgba(0, 0, 0, 0)!important;
}


/*___ Highlight Background __________________________________________________*/
.sidebarInTheaterOpening:hover,
ul.redDot li:hover {
	border-left-color: #434343!important;
	background: linear-gradient(to right, #383838, #0000)!important;
}


/*___ Faded Background bottom (Usually expand paragraph) ______________________*/
selector {
	background: linear-gradient(to bottom, rgba(255, 255, 255, 0), rgba(120, 224, 240, 0.13))!important;
}


/*________________________________________________________________________*/
selector {
	background-image: none!important;
}


/*________________________________________________________________________*/
pre,
code {
	background: #111!important;
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
}


/*__ Blue __________________________________________________________________*/
#show-more-btn .mb-load-btn {
	background-image: linear-gradient(to bottom right, #05497F, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

#show-more-btn .mb-load-btn:hover {
	background-image: linear-gradient(to bottom right, #06508F, #000)!important;
	color: #ff0!important;
}

#show-more-btn .mb-load-btn:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}


/*__ Red __________________________________________________________________*/
Selector {
	background-image: linear-gradient(to bottom right, #7f0505, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #8f1616, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}


/*__ Yellow ________________________________________________________________*/
#mainColumn .showtimes-body .showtimes-presentations .presentation .btn-showtimes,
#showtimes .showtimesContainer .showtimes-presentations .presentation .btn-showtimes,
.btn-primary {
	background-image: linear-gradient(to bottom right, #956101, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

#mainColumn .showtimes-body .showtimes-presentations .presentation .btn-showtimes:hover,
#showtimes .showtimesContainer .showtimes-presentations .presentation .btn-showtimes:hover,
.btn-primary:hover {
	background-image: linear-gradient(to bottom right, #a67212, #000)!important;
	color: #ff0!important;
}

#mainColumn .showtimes-body .showtimes-presentations .presentation .btn-showtimes:active,
#showtimes .showtimesContainer .showtimes-presentations .presentation .btn-showtimes:active,
.btn-primary:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}


/*__ Light Blue _____________________________________________________________*/
selector {
	background-image: linear-gradient(to bottom right, #4d87bc, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #5e98cd, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}


/*__ Green _________________________________________________________________*/
selector {
	background-image: linear-gradient(to bottom right, #81BC2E, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #92cd3f, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}


/*__ Plain Black ____________________________________________________________*/
#content-column,
.articleContentBody,
.home-body #videoPanel,
.media-browser-left,
.movie_list,
.panel:not(.bannerCaption),
[id*="homepage-"] {
	background-color: #000!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

#content-column:hover,
.articleContentBody:hover,
.home-body #videoPanel:hover,
.media-browser-left:hover,
.movie_list:hover,
.panel:not(.bannerCaption):hover,
[id*="homepage-"]:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}


/*__ Plain _________________________________________________________________*/
#boxoffice-list,
#opens-on-collection,
#other-list,
#theater-lists,
.col-sm-12,
.countdown-item,
.dropdown-menu,
.mb-movies .mb-movie,
.freshPicks .cfpItem,
.movie-details-row,
.newsItem.col-sm-6,
.newsItem.col-sm-8.col-full-xs {
	background-color: #111!important;
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0, 10px 10px 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
}

#boxoffice-list:hover,
#opens-on-collection:hover,
#other-list:hover,
#theater-lists:hover,
.col-sm-12:hover,
.countdown-item:hover,
.dropdown-menu:hover,
.freshPicks .cfpItem:hover,
.mb-movies .mb-movie:hover,
.movie-details-row:hover,
.newsItem.col-sm-6:hover,
.newsItem.col-sm-8.col-full-xs:hover {
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}


/*__ Noise ________________________________________________________________*/
.footer {
	background-image: linear-gradient(to bottom, rgba(0, 0, 0, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
	background-repeat: repeat-x, repeat!important;
	background-size: 100px 15px, 140px 140px!important;
}

.footer:hover {
	background-image: linear-gradient(to bottom, rgba(255, 255, 255, .1), rgba(0, 0, 0, 0)), url(https://image.ibb.co/e39YEk/noise2.jpg)!important;
}


/*__ Red Gradient ___________________________________________________________*/
.countdown-index,
.navbar.navbar-rt {
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}


/*__ Blue Gradient __________________________________________________________*/
selector {
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}


/*__ Green Gradient _________________________________________________________*/
#browser-navigator .navigator-menu li.active a,
.carousel .items .item.active {
	background: linear-gradient(to bottom, #15534d 0%, #000 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
	text-shadow: 1px 1px #555!important;
}


/*__ Yellow Gradient ________________________________________________________*/
.videoSection .panel-heading {
	background: linear-gradient(to bottom, #c0c00c 0%, #766102 100%) !important;
	border-bottom: 1px solid #000 !important;
	border-radius: 22px !important;
}


/*__ Shadow _______________________________________________________________*/
.movie-details-row img,
img {
	box-shadow: 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
}

@-moz-document domain("rottentomatoes.com") {
/*________________________________________________________________________*/
.articleContentBody p,
.col-sm-12,
.col-xs-12,
.col-xs-24,
.countdown-item,
.freshPicks .cfpItem,
.mb-movies .mb-movie,
.media,
.movie-details-row,
.navbar.navbar-rt #menu .menuHeader .dropdown-menu a,
.newsItem.col-sm-6,
.sidebarInTheaterOpening,
ul.redDot li {
	filter: brightness(75%)!important;
}


/*________________________________________________________________________*/
.articleContentBody p:hover,
.col-sm-12:hover,
.col-xs-12:hover,
.col-xs-24:hover,
.countdown-item:hover,
.freshPicks .cfpItem:hover,
.mb-movies .mb-movie:hover,
.media:hover,
.movie-details-row:hover,
