css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://www.krombacher.de/spass/bundesliga") {

/* unnötige Teile */
#communitySection,
#headerSection,
#stageContainer,
#mainNavigation,
div.socialBox,
#communityContainer,
img.bgMotiv,
#footerBody,
div.mainSection h1,
div.mainSection h3 {display:none !important}

/* unnötiger Hintergrund */
#contentSection,
#contentBody,
#sharpen {background:#eee !important}

/* dunkle Schrift statt hell auf hellgrün */
strong,td.head,th {color:#304000 !important}
span.spielfuehrer {color:#000 !important}
div.tabNavWrapper a {color:#304000 !important}

/* unnötiger Text "einfach etwas auswählen" */
span.rundentxt {font-size:1em !important;width:300px !important;display:none !important}

/* unnötige Bilder am Seitenrand unter /aktuell/ */
div.sideSection img {display:none !important}

}
