css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("engadget.com") {

.nav .item, h1, .feature-title, .review, .hed,.site-tools a {
    font-family: Arial Narrow,Arial,Helvetica,sans-serif !important;
    font-weight: normal !important;
}
.time-stamp{
    font-family: Verdana !important;
}
.feature-title {
    font-size: normal !important;
    font-family: Verdana !important;
}
.site-tools a {
    font-family: Verdana !important;
    font-size: normal !important;
}
h1 {
    font-size:x-large !important;
}

h3 {
    font-size: small !important;
    font-family: Verdana !important;
}

.main_hero_ftr { display: none; }
.topLeader { display: none; }
#outerslice { display: none; }
.engadget_hdr_hdr { display: none; }
.main_hero { padding-bottom: 0px!important; }
#topstories { display: none; }
.engadget_hdr { padding-top: 30px!important; }
.post_title a {
     font-size:30px!important; 
     font-family:Arial, Helvetica, sans-serif!important;
     font-weight:bold;
}
.post_body {
     font-size:12px!important;
     font-family:Arial, Helvetica, sans-serif!important;
}
.sponsorArea { display: none!important; }
.inpostad { display: none!important; }
}
