@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.torrentleech.org") {

* {
	font-family: Ubuntu, Sans, "helvetica", "sans-serif", "Segoe UI" !important;
	font-size: 8pt !important;}

/* REDESIGN LINKS - REMOVE UNDERLINES FROM ANY LINKS */
* {text-decoration: none !important;}

/* REDESIGN LINKS - LINK COLOR */
a:link {color: #CCE389 !important;
font-size: 8pt !important;
  /*font-weight: bold;*/}

/* REDESIGN LINKS - VISITED */
a:visited {color: DimGray !important;}

/* REDESIGN LINKS - HOVER */
a:hover {
   /*  border-bottom: 1px dotted #e1dac8 !important; */
        color: #ECECEC !important;}

/* DISABLE SUPPORT TL AD */
[class="ui-widget ui-widget-content ui-corner-all"] {
display: none !important;}

/* CAT & FILTERS TEXT */
.ui-button-text-only .ui-button-text {color: SILVER !important;
text-transform:uppercase !important;}


}
