css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.wykop.pl/mikroblog'), url-prefix('http://www.wykop.pl/wpis'), url-prefix('http://www.wykop.pl/ludzie'), url-prefix('http://www.wykop.pl/moj'), url-prefix('http://www.wykop.pl/szukaj'), url-prefix('http://www.wykop.pl/dodatki/pokaz/'), url-prefix('http://www.wykop.pl/tag/') {

.newtag {
    background:transparent!important;
    border:0!important;
    box-shadow:none!important;
    text-shadow:none!important;
}

.newtag span:first-child {
    padding-right:0!important;
}

.newtag .number {
    border:0!important;
    padding-left:0!important;
    opacity:0.3!important;
}

.newtag:hover .number {
    opacity:1!important;
    transition:0.2s;
}

}
