css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("netafull.net") {

.entry_head_ads,
.entry_head_share_btn,
.top_banner,
#sub_conteinner,
#footer_area,
#zenback
{
	display: none !important;
}
#main_conteiner
{
	width: 90% !important;
}
.entry_head,
.entry_meta,
.newentry,
.pagenate
{
	width: 100% !important;
}
.postbody > p > img
{
	display: block !important;
	margin: 0px auto !important;
}
.fb-comments
{
	display: block !important;
	width: 420px !important;
	margin: 0px auto !important;
}

}
