css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("turbobit.net") {
  /* clear page background (to speedup view page, to escape link) */
  /* ページ背景を削除する(ページ表示の高速化のため、ページ背景リンクを解除するため) */
  html { background: none !important; }

  /* hide top ads (download dummy) */
  /* 上部広告(ダミーの「Download」）の非表示 */
  div.download-top /*iframe[src^='http://tag.tlvmedia.com/?']*/
  { display: none; }
  
  /* hide main area bottom ads */
  /* メインエリア下部広告の非表示 */
  div.box.center>center>div[style='float:left']
  { display: none; }
  
  /* hide right pain (right ads area) */
  /* 右部ペインの非表示（広告部） */
  td.col-2
  { display: none; }
  
  /* hide bottom popup ads */
  /* ポップアップしてくる下部広告を非表示 */
  div[id^='banner_middle_']
  { display: none; }
}
