@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

toolbarseparator {
	-moz-appearance: none!important;
	border: 0!important;
	margin-left: 1px;
	padding: 0 1px;
	background-image: -moz-linear-gradient(rgba(0,0,0,0),rgba(0,0,0,.3),rgba(0,0,0,0)),
	                  -moz-linear-gradient(rgba(255,255,255,0) ,rgba(255,255,255,.5),rgba(255,255,255,0));
	background-repeat: no-repeat;
	background-position: left 0, 1px 0;
	background-size: 1px 100%;
}
