css:
/*
 *  "Kristen Stewart [Tumblr dash theme]" (http://userstyles.org/styles/79347)
 *  from "idkwhatimdoing" (http://userstyles.org/users/167284)
 *    by emaras (http://userstyles.org/users/167284)
 */
@-moz-document
	url-prefix('http://www.tumblr.com/'),
	url-prefix('https://www.tumblr.com/')
{

/* blue fix */
div.search_form_row {background-color: rgba(234, 228, 204, 0.6)}
div.l-header .selection_nipple {border-bottom-color: rgba(234, 228, 204, 0.6)}

#left_column, #right_column {
	background-color: transparent;
}
.controls_section.user_list li.item .follow_list_item_blog:before {
  background-image: none;
  border-right: none;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
#right_column:after {display:none}

.controls_section.user_list li.item .follow_list_item_blog:before {
  background: linear-gradient(to right, rgba(147, 65, 55, 0) 0, rgba(147, 65, 55, 1) 100%);
  border-right: rgba(147, 65, 55, 1);
}

.blog.has_follow_button:before,
#right_column:after {display: none}

/* fig.1, fig.4 */
  .l-content {
    /*opacity: 0.3;*/
    background-color: rgba(245, 242, 230, 0.7) !important;
    background-color: rgba(234, 228, 204, 0.6) !important;
  }
.selection_nipple {
  border-bottom-color: rgba(234, 228, 204, 0.6) !important;
  }

/* fig.2, fig.3 */
/*  body {
    background: #f5f2e6 url(http://media.share.pho.to/Dk4u/a1269057_l.png)
      no-repeat bottom left fixed! important;
    background-size: contain !important;
    background-position: -20% bottom !important;
    background: #f5f2e6 !important;
  }*/
/*http://i1255.photobucket.com/albums/hh634/meelakunis/dashthem2.png*/
  body:before {
    content: "";
    position: fixed; height: 100%; width: 28%;
    background: #f5f2e6 /*url(http://media.share.pho.to/Dk4u/a1269057_l.png)*/
      /*url('http://i1255.photobucket.com/albums/hh634/meelakunis/dashthem2.png')*/
      url('http://25.media.tumblr.com/63cbc5255490989a99b6759ff49d9222/tumblr_mwqenwSVIy1rxghfko1_400.png')
      no-repeat bottom right;
    background-size: auto 100%;
    z-index: -1;
  }
  body {
    background: #f5f2e6 !important;
  }

/* fig.5 */
  #right_column .controls_section li a {
    color: rgba(147, 65, 55, 0.6) !important;
  }
  #right_column .controls_section li a:hover {
    color: rgba(29, 32, 31, 1) !important;
  }

  .permalink {
    background: none !important;
  }
}
