css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.freelancer.com") {

#ns-sky {
    background: url("http://subtlepatterns.subtlepatterns.netdna-cdn.com/patterns/bedge_grunge.png") !important;
}

#ns-clouds {
    background: none !important;
}
#ns_wrapper {
    background: url("http://subtlepatterns.subtlepatterns.netdna-cdn.com/patterns/creampaper.png") !important;
    border-left: 1px solid #BDBDBD !important;
    border-right: 1px solid #BDBDBD !important;
    border-bottom: 1px solid #BDBDBD !important;
    box-shadow: 0 0 3px 2px #D1D1D1 !important;
}
.ns-sticker {
    background: none !important;
}
.c9d-tabcnt {
    background: none !important;
}
#profile_part_id, .notice_board_part, .project_view_part {
    background: #EFEFEF !important;
    border: 1px solid #BDBDBD !important;
}
#ns_footer, #ns_footer-text {
    display:none !important;
}
.dataTable th {
    background-color: #5F5E5E !important;
    background-clip: padding-box !important;
}
.global-announcement.vworker {
    display:none !important;
}
}
