css:
@-moz-document url-prefix("http://support.bullseye.com.au") {
td { font-size: 9pt !important; padding: 0px !important; }
table.width100 tr td:nth-child(12), table.width100 tr td:nth-child(14) { display:none; }
table.width100 input[name="custom_field_10"],table.width100 input[name="custom_field_15"],table.width100 input[name="summary"] { width:1440px; }

table.width75 input[name="custom_field_10"],table.width75 input[name="custom_field_15"],table.width75 input[name="summary"] { width:1000px; }

textarea[name="description"], textarea[name="additional_information"] { width:600px; height: 400px; }

table#buglist tbody tr[bgcolor]:hover { background-color: #FF8800 !important; }

form[name="filters"] select[multiple] { width: 250px; }
form[name="filters"] select[name="custom_field_10[]"] { position: absolute; width: 1200px; }
form[name="filters"] table.width100 td { vertical-align:top; }

tr:hover { background-color: #FF8800 !important; }
}
