css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {
@media (min-width: 1800px) {
#yt-masthead,
#masthead-appbar,
#footer {
    width: 1280px  !important;
    margin: 0 auto  !important;
}
.yt-lockup.yt-lockup-tile {
    max-width: 100% !important
}
.result-list .yt-lockup-thumbnail,
.result-list .yt-lockup-thumbnail .yt-thumb-185,
.result-list .yt-lockup-thumbnail .yt-thumb-clip img,
.channels-browse-content-list .yt-lockup-thumbnail,
.channels-browse-content-list .yt-lockup-thumbnail .yt-thumb-185,
.channels-browse-content-list .yt-lockup-thumbnail .yt-thumb-clip img {
    width: 220px !important;
}
.yt-lockup.yt-lockup-tile .yt-lockup-thumbnail {
    margin-right: 15px !important;
}

.result-list .yt-lockup-title,
.channels-browse-content-list .yt-lockup-title {
    margin-top: 10px !important;
    margin-bottom: 7px !important;
    font-size: 17px !important;
    letter-spacing: -0.03em !important;
}
.watch-wide,
.watch-medium,
.watch-wide #watch7-video-container {
    width: 1280px !important;
}
.watch-wide #watch7-video,
.watch-wide #watch7-player,
.watch-medium #player-api,
.watch-medium .player-api,
.watch-medium .video-stream {
    width: 1280px !important;
    height: 750px !important;
}
.watch-medium .video-stream {
    top: 0 !important;
    left: 0 !important;
    height: 720px !important;
}
.branded-page-v2-secondary-col {
    display: none !important;
}
}
}
