@-moz-document domain("tumblr.com") {
::-webkit-scrollbar-thumb,
body::-webkit-scrollbar-thumb,
ol::-webkit-scrollbar-thumb,
ul::-webkit-scrollbar-thumb,
#transcript-scrollbox::-webkit-scrollbar-thumb {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border-radius: 22px!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar-thumb:hover,
body::-webkit-scrollbar-thumb:hover,
ol::-webkit-scrollbar-thumb:hover,
ul::-webkit-scrollbar-thumb:hover,
#transcript-scrollbox::-webkit-scrollbar-thumb:hover {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-track,
#messages div::-webkit-scrollbar-track {
    /* Color of slider */
    background: #170000!important;
    box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar,
#messages div::-webkit-scrollbar {
    /* Width */
    width: .8em!important;
}
 
::-webkit-scrollbar-button,
#messages div::-webkit-scrollbar-button {
    /* Color of handles at top and bottom */
    background: #4c0707!important;
    box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-button:hover,
#messages div::-webkit-scrollbar-button:hover {
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
}

@-moz-document domain("tumblr.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

selector {
	text-transform: capitalize!important;
}

.blog-sub-nav-details .blog-sub-nav-item.currentItem,
.blog-sub-nav-details .blog-sub-nav-item.selected,
.blog-sub-nav-details .blog-sub-nav-item:hover,
.identity .controls_section.user_list li .follow_list_item_blog:before,
.popover_gradient>.popover_inner,
.signup_forms .form_row,
:focus {
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

.compose-button {
	fill: #0000!important;
}



/*___ Fonts ________________________________________________________________________*/

.post-content-text h1, .post.is_conversation .post_content .post_title, .post.is_regular .post_content .post_title, .post .post-content-text .title-field *, .post .post_body h1, .reblog-content h1,
.about-tumblr-showcase .section-title,
.post .post_body h2,
.post .post_body h4,
.post .post_body h5,
.post .post_body h6,
.post .post_body,
.post-content-text h2,
.post-content-text h4,
.post-content-text h5,
.post-content-text h6,
.post-content-text,
.post_full .post_footer,
.reblog-content h2,
.reblog-content h4,
.reblog-content h5,
.reblog-content h6,
.reblog-content,
body {
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

.popover--account-popover .popover .popover_menu_item .popover_menu_item_anchor .popover_menu_item_anchor_icon {
	font-size: 34px!important;
}

selector {
	font-size: 21px!important;
}

.reblog-content {
	font-size: 19px!important;
}

.post .post_body h4,
.post .post_body h5,
.post .post_body h6,
.post .post_body,
.post-content-text h4,
.post-content-text h5,
.post-content-text h6,
.post-content-text,
.reblog-content h4,
.reblog-content h5,
.reblog-content h6 {
	font-size: 17px!important;
}

.post-composer_note-post .answerer .name,
.post-composer_note-post .asker .name,
.post_full.is_note .answerer .name,
.post_full.is_note .asker .name {
	font-size: 15px!important;
	line-height: 27px!important;
}

.reblog-content p {
	line-height: 28px!important;
}

/*___ Remove Items _________________________________________________________________*/

.post_brick .post_tags:after {
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.popover--account-popover .popover .popover_menu_item .popover_menu_item_anchor .popover_menu_item_anchor_icon {
	margin-top: 5px;
}

/*___ Margins ______________________________________________________________________*/

.l-container.l-container--two-column .right_column,
.l-container.l-container--two-column-dashboard .left_column,
.l-container.l-container--two-column-dashboard .right_column,
.showcase .blogs-section .blogs-post,
.signup_forms .form_row {
	margin: 12px!important;
}

selector {
	margin: 3rem!important;
}

/*___ Paddings _____________________________________________________________________*/

.discover .tags-and-tumblelogs,
.l-container.l-container--two-column .right_column,
.l-container.l-container--two-column-dashboard .left_column,
.l-container.l-container--two-column-dashboard .right_column,
.post.post_full,
.rapid-recs-container .rapid-recs .rapid-rec-inner,
.showcase .blogs-section .blogs-post {
	padding: 12px!important;
}

selector {
	padding: 6px!important;
}

/*___ Heights ______________________________________________________________________*/

selector {
	max-height: none!important;
}

selector {
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

.discover .discover-content,
.post_full.is_recommended .post_info_recommended,
body {
	max-width: none!important;
}

selector {
	width: auto!important;
}

.post.post_full {
	width: 100%!important;
}

.discover .discover-posts .posts-holder,
.l-container.l-container--two-column,
.l-container.l-container--two-column-dashboard,
.showcase .blogs-section .section-wrapper {
	width: 98%!important;
}

.post-composer_note-post .note_item,
.post_full.is_note .post-body .note_item,
.post_full.is_note .post_body .note_item {
	width: 95%!important;
}

.l-container.l-container--two-column-dashboard .left_column,
.l-container.l-container--two-column .left_column {
	width: 77%!important;
}

.indash_tumblelog_compact {
    width: 12%!important;
}

.showcase .blogs-section .blogs-post-column {
	width: 21rem!important;
}
}

@-moz-document domain("tumblr.com") {
/*__________________________________________________________________________________*/

a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.blog-selector-container .selected-blog .name,
.blog-sub-nav-details .blog-sub-nav-item.currentItem .blog-sub-nav-item-label,
.blog-sub-nav-details .blog-sub-nav-item.selected .blog-sub-nav-item-label,
.blog-sub-nav-details .blog-sub-nav-item:hover .blog-sub-nav-item-label,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.post_brick .post_notes_label .note_link_current,
.grey_note,
.post_full .post_footer,
.post_full .post_header .post_info .post_info_link,
.post_full .post_header,
.radar .radar_controls .note_count>a,
li,
p {
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

.post-content-text h1,
.post.is_conversation .post_content .post_title,
.post.is_regular .post_content .post_title,
.post .post-content-text .title-field *,
.post .post_body h1,
.reblog-content h1,
.identity .controls_section li.section_header,
.popover_header,
.post-composer_note-post .answerer .name,
.post-composer_note-post .asker .name,
.post_full.is_note .answerer .name,
.post_full.is_note .asker .name,
h1,
h2 {
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

.post.is_quote .quote:not(.post_chrome),
h3,
h4 {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

h5,
h6,
label {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

.last_updated,
time {
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/

selector {
	color: #07ff24!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/

.no_posts_found {
	color: #ff0707!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder {
	/* Chrome, Firefox, Opera, Safari 10.1+ */
	color: #f48f8f;
	opacity: 1;
	/* Firefox */
}

/*___ Code _________________________________________________________________________*/

.token.punctuation {
	color: #fff!important;
}

.token.selector,
.token.attr-name,
.token.string,
.token.char,
.token.builtin,
.token.inserted {
	color: #af0!important;
}

.token.atrule,
.token.attr-value,
.token.keyword {
	color: #00b3ff!important;
}

.token.property,
.token.tag,
.token.boolean,
.token.number,
.token.constant,
.token.symbol,
.token.deleted {
	color: #ff6cbe!important;
}
}

@-moz-document domain("tumblr.com") {
/*__________________________________________________________________________________*/

