@-moz-document domain("tumblr.com") {
/*__________________________________________________________________________________*/

.post_brick,
.follower,
.identity .controls_section li,
#posts.posts>.post_container,
.rapid-recs-container .rapid-recs .micro-post-list .micro-post-item,
.showcase .blogs-section .blogs-post,
.showcase .create-section .create-graphic .post-icon,
.tab_bar .tab
{
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

.post_brick:hover,
.follower:hover,
.identity .controls_section li:hover,
#posts.posts>.post_container:hover,
.rapid-recs-container .rapid-recs .micro-post-list .micro-post-item:hover,
.showcase .blogs-section .blogs-post:hover,
.showcase .create-section .create-graphic .post-icon:hover,
.tab_bar .tab:hover
{
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

.post_avatar .post_avatar_link,
img {
	border-radius: 22px!important;
}

.follower:last-child {
	border-bottom-left-radius: 22px;
	border-bottom-right-radius: 22px;
}

/*__________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________


li,
p,
