css:
@-moz-document domain('trello.com') {
  .card-short-id {
    display: inline !important;
    border: 1px solid #d9d9d9;
    border-bottom-color: #d3d3d3;
    border-radius: 2px;
    color: #737373;
    cursor: default;
    float: left;
    font-size: 11px;
    line-height: 16px;
    height: 16px;
    margin-right: 4px;
    padding: 0 4px;
    position: relative;
    text-decoration: none;
  }
}
