css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.unisa.edu.au") {
	img {
		border-radius: 10px;
	}
    #nav {
		border-radius: 20px 20px 0 0;
	}
	ul#slider li, ul#slider li img {
		border-radius: 20px;
	}
	div#banner-nav {
	    right: 45px !important;
	}
	div#banner-nav a {
		border-radius: 10px;
		height: 15px !important;
        width: 15px !important;
        color: transparent !important;
        margin-left: 2px !important;
	}
	div.inline-banner ul li {
		border-radius: 20px;
	}
	div.inline-banner ul li img{
		border-radius: 0;
	}
	.inline-banner #banner-nav {
		left: auto !important;
		right: 50px !important;
	}
	#main .search_site {
		border-radius: 20px;
	}
	#main .date-block {
		border-radius: 10px;
	}
	#study-research .areas li a.sctn {
		border-radius: 10px;
	}
	#news-events .scroller {
		border-radius: 20px;
	}
	#news-events .scroller img {
		border-radius: 15px;
		opacity: 0.8;
	}
	#news-events .scroller img:hover {
		opacity: 1;
	}
	#footer {
		background: none no-repeat scroll right bottom #030399 !important;
		border-radius: 0 0 20px 20px;
	}
	#footer {
		background-image: none;
	}
	#menus .menu {
		border-radius: 0 0 20px 20px;
	}
	#menus .cta {
		border-radius: 0 0 20px 0;
	}
	#menus .information {
		border-radius: 0 0 0 20px;
	}
	#menus .sub_categories img, #menus .sub_categories .heroshot {
		border-radius: 10px 10px 10px 10px;
	}
	.custom-content-area {
		border-radius: 5px;
	}
	ul.content-grid li img {
		opacity: 0.8;
	}
	ul.content-grid li img:hover {
		opacity: 1;
	}
	#hero-banner, #hero-banner ul {
        height: 280px !important;
    }
}
