css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ask.fm"){

html {
background-color: #777;
}

body {
background-color: transparent;
}

#menu {
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#dfe5d7), color-stop(100%,#888));
border-bottom: 1px solid black;
}

a.link-menu {
-webkit-border-radius-bottom: 5px 11px;
color: black;
padding-bottom: 15px;
}

a.link-menu:hover {
color: black;
-webkit-box-shadow:inset grey 0px 0px 50px;
}

#logo {
margin-right: 30px;
padding-bottom: 0px;
}

#logo:hover {
padding-top: 0px;
-webkit-box-shadow:inset grey 0px 0px 50px;
}

a.notification-like {
-webkit-border-radius: 30px;
border: 2px solid transparent;
}

a.notification-like:hover {
border: 2px solid grey;
}

a.notification-question {
-webkit-border-radius: 30px;
border: 2px solid transparent;
}

a.notification-question:hover {
border: 2px solid grey;
}

a.notification-answer {
-webkit-border-radius: 30px;
border: 2px solid transparent;
}

a.notification-answer:hover {
border: 2px solid grey;
}

}
