css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.imageporter.com") {
  /* hide top image under menus */
  /* 上部メニューに付属する画像を非表示にします */
  img.banner { display: none; }

  /* hide upper ads on image */
  /* 画像上部広告を非表示にします */
  iframe[src^="http://whaleserver.com/"] { display: none; }

  /* hide under ads on image */
  /* 画像下部広告を非表示にします */
  iframe[src^="http://feeds.videosz.com/"] { display: none; }

  /* hide under ads on "Continue to Image" */
  /* 「Continue to Image」の下部広告を非表示にします */
  iframe[src="about:blank"] { display: none; }
}
