css:
@-moz-document domain(youtube.com)
{
	#player,#watch-header{margin-top:0!important;}
	#watch-appbar-playlist{margin-left:0!important;}
    #guide-container{border:1px solid darkgray!important; border-left:none!important; border-top:none!important;}
    #masthead-appbar-container{border-bottom:1px solid darkgray!important;border-top:1px solid darkgray!important;
    background:white;}
	#content{	box-shadow: none;}
    
	#body-container,#yt-masthead-container,#page-container,#footer-container{background:url("http://data.pryx.net/stylish/noise.png") repeat !important;}
	#footer-container, #footer-main,#yt-masthead-container{border:none!important;}
	#player{margin-bottom:0!important;}
    #theater-background{background:black!important;}

.yt-card{border: 1px solid #bbb;}
	#watch7-action-buttons{border-color:rgba(0,0,0,0.3)!important; border-left:none!important; border-right:none!important;}
	#content{background:white!important; border: 1px solid rgba(0,0,0,0.3) !important;}
	.feed-item-main{border-bottom: 1px solid rgba(0,0,0,0.3)!important;}
	hr{margin:0!important;border-top: 1px solid rgba(0,0,0,0.2)!important;}
}

@-moz-document regexp("http?://www\\.youtube\\.com/watch.*"), regexp("https?://www\\.youtube\\.com/watch.*") 
{
    #yt-masthead-container{border-bottom:solid darkgray 1px!important;}
        #masthead-appbar-container{border:none!important;}
    #guide-container{border:1px solid darkgray!important; border-left:none!important;}
    #guide{background:none!important;border:none!important;}
#content{background:url("http://data.pryx.net/stylish/noise.png") repeat !important; width:0%!important; border:none!important;}


}
