css:
@-moz-document domain("youtube.com") {
/* Global ReCentering */
#yt-masthead-container #yt-masthead,
#header #masthead-subnav,
#header #ad_creative_1,
#page-container #page {
    margin-left: auto !important;
    margin-right: auto !important;
    transition: none !important;
}

/* Element shrinking */
#page-container #page,
#yt-masthead-container #yt-masthead {
    min-width: 1003px !important;
    max-width: 1190px !important;
    width: auto !important;
}

/* Masthead align */
#yt-masthead-container #logo-container {
    margin-right: 108px !important;
}

/* Footer */
#footer-container {
    text-align: center !important;
}

#footer-container #footer {
    display: inline-block !important;
    text-align : start !important;
}


/* Video main page */
#page-container #page.watch {
    max-width: 100% !important;
    overflow: hidden !important;
}

.site-left-aligned.guide-enabled #player,
.site-left-aligned.guide-enabled #watch7-main-container {
    padding-right: 225px !important;
}

#page.watch #player,
#page.watch #watch7-main,
#page.watch .watch7-playlist-bar {
    text-align: center !important;
    font-size: 0 !important;
}

#page.watch .watch-content,
#page.watch .watch-sidebar,
#page.watch .watch7-playlist-bar > *,
#page.watch #playlist-tray {
    display: inline-block !important;
    float: none !important;
    font-size: 13px !important;
    text-align: start !important;
    vertical-align: top !important;
}

#page.watch .watch-content {
    margin-right: 400px !important;
}

#page.watch .watch-sidebar {
    margin-left: -400px !important;
    width: 400px !important;
}

#page.watch .watch7-playlist-bar-right {
    transition: none !important; 
}

#page.watch .hid.watch7-playlist-interstitial {
    display: none !important;    
}

/* Wide player */
#player.watch-medium .watch-content,
#player.watch-medium.watch-playlist-collapsed .watch7-playlist-bar-right {
    margin-right: 186px !important;
}

#player.watch-medium .watch-sidebar {
    margin-left: -186px !important;
    width: 300px !important;
}

#player.watch-medium #watch7-playlist-tray-container {
    left: -114px !important;
}

#player.watch-medium .watch7-playlist-bar-left {
    transition: width .3s ease-in-out !important;
    width: 740px !important;
}

#player.watch-medium .watch7-playlist-bar-right {
    transition: margin-right .3s ease-in-out !important;
    margin-right: 0 !important;
}

#player.watch-medium.watch-playlist-collapsed .watch7-playlist-bar-left {
    width: 554px !important;
}
}
