css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pandora.com") {

    #mainContentContainer,
    #footer,
    .adSupported-layout #adLayout{ width: 100% !important; }
    
    #pandoraRibbonContainer,
    .art[src*="/ads/"],
    .platformPromo,
    #promobox,
    #ad_container{ display:none !important; }
    
    body{ background-color: #09102A !important; }
    
    .skinContainer,
    #topnav{ top: 0 !important; }

    #mainContainer{ background-image:none !important; }
    
    #main{ top: 95px !important; }
    
    .contentContainer .top{ border-radius: 0 !important; }
    
    .slidesBackground{
        background: linear-gradient(to bottom,  #031326 0%,#022650 13%,#022650 78%,#031326 100%); /* W3C */ 
        width: 100%;
    }
    
    .slidesBackground img{ visibility: hidden; }
    
    .stationListHolder{ 
        padding: 0 0 31px !important; 
        min-height: 699px !important; 
    }
    
    #stationSortButtons{
        position: absolute;
        bottom: 0;
    }
    
}
