css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.redhotpawn.com"),
domain("www.chessatwork.com"),
domain("www.timeforchess.com"),
domain("www.redhotchess.com"),
domain("www.playtheimmortalgame.com")

{

/* applies font to all generic HTML tags */
    a,
    p,
    label,
    html,
    body,
    span,
    div,
    table,
    tr,
    td,
    br,
    input,
    font,
    button,
    select,
    option {
        font-family: Tahoma !important;
        font-size: 8pt !important;
    }

}
